client/src/styles/astrology.css.content:
/* Custom animations for celestial effects */
@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes twinkle {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

@keyframes rotate-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes cosmic-pulse {
  0%, 100% { 
    box-shadow: 0 0 20px rgba(139, 92, 246, 0.3);
    transform: scale(1);
  }
  50% { 
    box-shadow: 0 0 40px rgba(139, 92, 246, 0.6);
    transform: scale(1.05);
  }
}

/* Celestial background effects */
.celestial-bg {
  background: radial-gradient(ellipse at center, rgba(139, 92, 246, 0.1) 0%, transparent 70%);
}

.star-field {
  background-image: 
    radial-gradient(2px 2px at 20px 30px, #ffffff, transparent),
    radial-gradient(2px 2px at 40px 70px, rgba(255, 255, 255, 0.8), transparent),
    radial-gradient(1px 1px at 90px 40px, #ffffff, transparent),
    radial-gradient(1px 1px at 130px 80px, rgba(255, 255, 255, 0.6), transparent),
    radial-gradient(2px 2px at 160px 30px, #ffffff, transparent);
  background-repeat: repeat;
  background-size: 200px 100px;
  animation: twinkle 4s ease-in-out infinite alternate;
}

/* Zodiac wheel specific styles */
.zodiac-wheel {
  animation: rotate-slow 120s linear infinite;
}

.zodiac-sign-button {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  backdrop-filter: blur(10px);
}

.zodiac-sign-button:hover {
  animation: cosmic-pulse 2s ease-in-out infinite;
}

/* Mystical text effects */
.mystical-text {
  text-shadow: 0 0 10px rgba(139, 92, 246, 0.5);
}

.golden-text {
  background: linear-gradient(45deg, #fbbf24, #f59e0b, #d97706);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Floating elements */
.float-animation {
  animation: float 6s ease-in-out infinite;
}

.float-animation:nth-child(2n) {
  animation-delay: -2s;
}

.float-animation:nth-child(3n) {
  animation-delay: -4s;
}

/* Custom scrollbar for horoscope content */
.horoscope-content::-webkit-scrollbar {
  width: 8px;
}

.horoscope-content::-webkit-scrollbar-track {
  background: rgba(139, 92, 246, 0.1);
  border-radius: 4px;
}

.horoscope-content::-webkit-scrollbar-thumb {
  background: rgba(139, 92, 246, 0.3);
  border-radius: 4px;
}

.horoscope-content::-webkit-scrollbar-thumb:hover {
  background: rgba(139, 92, 246, 0.5);
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .zodiac-sign-button {
    width: 60px;
    height: 60px;
  }

  .mystical-text {
    font-size: 2rem;
  }
}

/* Gradient borders for cards */
.cosmic-border {
  position: relative;
}

.cosmic-border::before {
  content: '';
  position: absolute;
  inset: -2px;
  background: linear-gradient(45deg, #8b5cf6, #06b6d4, #10b981, #f59e0b);
  border-radius: inherit;
  opacity: 0.7;
  z-index: -1;
  animation: rotate-slow 20s linear infinite;
}

/* Particle effect overlay */
.particles {
  position: absolute;
  width: 100%;
  height: 100%;
  pointer-events: none;
}

.particle {
  position: absolute;
  width: 2px;
  height: 2px;
  background: rgba(255, 255, 255, 0.8);
  border-radius: 50%;
  animation: float 8s linear infinite;
}

